export const colors = {
  bg: "#F1F7F2",
  card: "#FFFFFF",
  accent: "#1C8F4A",
  accentSoft: "#E0F4E7",
  danger: "#E84343",
  text: "#1F2933",
  textSoft: "#6B7B7C",
  border: "#E2E8F0",
  tabBg: "#FFFFFF",
  tabIcon: "#7B8A86"
};

export const radius = {
  card: 24,
  pill: 999
};

export const spacing = (n = 1) => 4 * n;
