import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions
} from "react-native";
import ScreenContainer from "../components/ScreenContainer";
import TokenBalanceWidget from "../components/TokenBalanceWidget";
import { colors, spacing, radius, typography } from "../theme/theme";
import { useNavigation } from "@react-navigation/native";
import { getPlants } from "../api/growlog";
import { getSubscription } from "../api/subscription";
import { getTrending } from "../api/posts";

const { width } = Dimensions.get("window");

// Helper functions for trending categories
function getCategoryIcon(category) {
  const icons = {
    nutrients: "üíß",
    lighting: "üí°",
    water: "üí¶",
    substrate: "üå±",
    soil: "ü™¥",
    hydro: "üåä",
    training: "‚úÇÔ∏è",
    harvest: "üèÜ",
    diagnosis: "üîç"
  };
  return icons[category?.toLowerCase()] || "üìù";
}

function getCategoryColor(index) {
  const colors = ["#3B82F6", "#F59E0B", "#10B981", "#8B5CF6", "#EF4444"];
  return colors[index % colors.length];
}

export default function DashboardScreen() {
  const navigation = useNavigation();
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [subscription, setSubscription] = useState({ plan: "free" });
  const [trending, setTrending] = useState([]);

  useEffect(() => {
    loadPlants();
    loadSubscription();
    loadTrending();
  }, []);

  async function loadPlants() {
    try {
      const data = await getPlants();
      setPlants(data || []);
    } catch (err) {
      console.error("Failed to load plants:", err);
    } finally {
      setLoading(false);
    }
  }

  async function loadSubscription() {
    try {
      const data = await getSubscription();
      setSubscription(data || { plan: "free" });
    } catch (err) {
      console.error("Failed to load subscription:", err);
    }
  }

  async function loadTrending() {
    try {
      const data = await getTrending();
      // Take top 4 trending posts/topics
      setTrending((data || []).slice(0, 4));
    } catch (err) {
      console.error("Failed to load trending:", err);
      // Fallback to static categories if API fails
      setTrending([]);
    }
  }

  const QuickAction = ({ icon, label, onPress, color }) => (
