  if (!ent?.ready) {
    return (
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <ActivityIndicator />
      </View>
    );
  }

  const target =
    ent.mode === "commercial"
      ? "/home/commercial"
      : ent.mode === "facility"
        ? selectedId
          ? "/home/facility"
          : "/home/facility/select"
        : "/home/personal";

  return <Redirect href={target} />;
