import React, {
  createContext,
  useContext,
  useEffect,
  useMemo,
  useState,
  useRef
} from "react";
import { logEvent } from "../api/events";
import { usePushRegistration } from "../hooks/usePushRegistration";
import {
  login as apiLogin,
  signup as apiSignup,
  type AuthUser,
  type SignupBody
} from "../api/auth";
import { setToken as persistToken, getToken as readToken } from "./tokenStore";
import { setAuthToken, setOnUnauthorized } from "../api/client";
import { apiMe } from "../api/me";

type AuthState = {
  token: string | null;
  user: AuthUser | null;
  ctx: any | null;
  isHydrating: boolean;
  isAuthed: boolean;
  login: (email: string, password: string) => Promise<void>;
  signup: (body: SignupBody) => Promise<void>;
  logout: () => Promise<void>;
};

const AuthContext = createContext<AuthState | null>(null);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [token, setToken] = useState<string | null>(null);
  const [user, setUser] = useState<AuthUser | null>(null);
  const [ctx, setCtx] = useState<any | null>(null);
  const [isHydrating, setIsHydrating] = useState(true);

  // Prevent rehydration loops - hydrate only once
  const didHydrateRef = useRef(false);
  // Prevent multiple logout operations in flight
  const isLoggingOutRef = useRef(false);

  // Hard logout that prevents token from reappearing
  const hardLogout = async () => {
    if (isLoggingOutRef.current) return; // Already logging out
    isLoggingOutRef.current = true;

    try {
      setAuthToken(null);
      setToken(null);
      setUser(null);
      setCtx(null);
      await persistToken(null);
    } finally {
      isLoggingOutRef.current = false;
    }
  };

  // Wire global 401 invalidation: any 401 from any endpoint (except login/signup) triggers hardLogout
  useEffect(() => {
    setOnUnauthorized(() => {
      void hardLogout();
    });
    return () => {
      setOnUnauthorized(null);
    };
  }, []);

  useEffect(() => {
    // Only hydrate once
    if (didHydrateRef.current) return;
    didHydrateRef.current = true;

    let mounted = true;
    (async () => {
      try {
        const t = await readToken();
        if (!mounted) return;

        if (t) {
          setToken(t);
          setAuthToken(t); // keep api client in sync

          // Try to validate token with backend
          try {
            const me = await apiMe();
            if (mounted) {
              setUser(me.user);
              setCtx(me.ctx ?? null);
            }
          } catch (e: any) {
            // Only hardLogout on 401 (token is invalid)
            if (e?.status === 401) {
              console.log("[AUTH] Token rejected by server (401), clearing");
              await hardLogout();
              return;
            }
            // Network/server errors: keep token, user stays null until backend recovers
            console.error("[AUTH] Failed to hydrate user (non-401):", e);
            // do NOT hardLogout - let app work offline or retry
          }
        }
      } finally {
        if (mounted) setIsHydrating(false);
      }
    })();

    return () => {
      mounted = false;
    };
  }, []);

  // Register push token after login/hydration
  usePushRegistration({
    userId: user?.id ?? null,
    token,
    isHydrating
  });

  async function login(email: string, password: string) {
    try {
      const res = await apiLogin({ email, password });
      setToken(res.token);
      setAuthToken(res.token); // critical
      setUser(res.user);
      await persistToken(res.token);
    } catch (err: any) {
      // Pass through normalized errors so UI can branch on code/status
      throw err;
    }
    // Fire analytics after successful auth (fire-and-forget)
    void logEvent("USER_LOGIN");
  }

  async function signup(body: SignupBody) {
    try {
      const res = await apiSignup(body);
      setToken(res.token);
      setAuthToken(res.token); // critical
      setUser(res.user);
      await persistToken(res.token);
    } catch (err: any) {
      // Pass through normalized errors so UI can branch on code/status
      throw err;
    }
    // Fire analytics after successful auth (fire-and-forget)
    void logEvent("USER_REGISTER");
  }

  async function logout() {
    await hardLogout();
  }

  const value = useMemo<AuthState>(
    () => ({
      token,
      user,
      ctx,
      isHydrating,
      isAuthed: !!token,
      login,
      signup,
      logout
    }),
    [token, user, ctx, isHydrating]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error("useAuth must be used inside <AuthProvider>");
  return ctx;
}

// Optional helper to display best error message in UI
export function getAuthErrorMessage(e: unknown) {
  if (e && typeof e === "object" && "message" in e) {
    return String((e as any).message);
  }
  if (typeof e === "string") return e;
  return "Something went wrong.";
}

import React from "react";
import { ActivityIndicator, View } from "react-native";
import { useRouter, useSegments } from "expo-router";
import { useAuth } from "./AuthContext";

export function RequireAuth({ children }: { children: React.ReactNode }) {
  const { token, user, isHydrating } = useAuth();
  const router = useRouter();
  const segments = useSegments();

  React.useEffect(() => {
    if (!isHydrating) {
      const inAuthGroup = segments[0] === "(auth)";
      if (!token || !user) {
        if (!inAuthGroup) router.replace("/(auth)/login");
        return;
      }
      if (inAuthGroup) router.replace("/(app)/home");
    }
  }, [isHydrating, token, user, segments]);

  if (isHydrating) {
    return (
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <ActivityIndicator />
      </View>
    );
  }

  return <>{children}</>;
}

import React, { useEffect, useState } from "react";
import { TouchableOpacity, Text } from "react-native";
import { followUser, unfollowUser, isFollowing } from "../api/users";

export default function FollowButton({ userId }) {
  const [following, setFollowing] = useState(false);

  useEffect(() => {
    let mounted = true;
    async function load() {
      try {
        const res = await isFollowing(userId);
        const payload = res?.data ?? res;
        const val = payload?.isFollowing ?? false;
        if (mounted) setFollowing(!!val);
      } catch (err) {
        // ignore
      }
    }
    load();
    return () => (mounted = false);
  }, [userId]);

  async function toggle() {
    try {
      if (following) {
        await unfollowUser(userId);
        setFollowing(false);
      } else {
        await followUser(userId);
        setFollowing(true);
      }
    } catch (err) {
      // ignore
    }
  }

  return (
    <TouchableOpacity
      onPress={toggle}
      style={{
        paddingHorizontal: 12,
        paddingVertical: 6,
        borderRadius: 8,
        backgroundColor: following ? "#ddd" : "#2ecc71"
      }}
    >
      <Text style={{ color: following ? "#333" : "white", fontWeight: "600" }}>
        {following ? "Following" : "Follow"}
      </Text>
    </TouchableOpacity>
  );
}

