type AnyTask = any;

export function usePersonalTasks() {
  const queryClient = useQueryClient();

  const tasksQuery = useQuery({
    queryKey: ["personalTasks"],
    queryFn: async () => {
      // normalize common shapes
      const res: any = await getTasks();
      if (Array.isArray(res)) return res as AnyTask[];
      if (Array.isArray(res?.data)) return res.data as AnyTask[];
      if (Array.isArray(res?.items)) return res.items as AnyTask[];
      if (Array.isArray(res?.tasks)) return res.tasks as AnyTask[];
      return (res ?? []) as AnyTask[];
    }
  });

  const createTaskMutation = useMutation({
    mutationFn: async (payload: any) => {
      return createCustomTask(payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["personalTasks"] });
    }
  });

  const completeTaskMutation = useMutation({
    mutationFn: async (taskId: string) => {
      return completeTask(taskId);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["personalTasks"] });
    }
  });

  return {
    ...tasksQuery,
    tasks: (tasksQuery.data ?? []) as AnyTask[],

    createTask: createTaskMutation.mutateAsync,
    creating: createTaskMutation.isPending,
    createError: createTaskMutation.error,

    completeTask: completeTaskMutation.mutateAsync,
    completing: completeTaskMutation.isPending,
    completeError: completeTaskMutation.error
  };
}

export function usePersonalTasks() {
  import * as React from "react";
  import { apiRequest } from "../api/apiRequest";

  type UsePersonalTasksResult<T = any> = {
    data: T | null;
    isLoading: boolean;
    error: any;
    refetch: () => Promise<void>;
  };

  export function usePersonalTasks<T = any[]>(): UsePersonalTasksResult<T> {
    const [data, setData] = React.useState<T | null>(null);
    const [isLoading, setIsLoading] = React.useState(true);
    const [error, setError] = React.useState<any>(null);

    const mountedRef = React.useRef(true);

    const refetch = React.useCallback(async () => {
      setIsLoading(true);
      setError(null);

      try {
        // Personal tasks list
        const out = await apiRequest<T>("/api/tasks", { method: "GET" });
        if (mountedRef.current) setData(out);
      } catch (e) {
        if (mountedRef.current) setError(e);
      } finally {
        if (mountedRef.current) setIsLoading(false);
      }
    }, []);

    React.useEffect(() => {
      mountedRef.current = true;
      void refetch();
      return () => {
        mountedRef.current = false;
      };
    }, [refetch]);

    return { data, isLoading, error, refetch };
  }
}

